mapper.select:
SELECT BOOK_NO, TITLE, AUTHOR, PRICE, PUBDATE
  FROM BOOK_T
 WHERE BOOK_NO = #{bookNo}

mapper.insert:
INSERT INTO BOOK_T (
    BOOK_NO
  , TITLE
  , AUTHOR
  , PRICE
  , PUBDATE
) VALUES (
    BOOK_SEQ.NEXTVAL
  , #{title}
  , #{author}
  , #{price}
  , SYSDATE
)

mapper.update:
UPDATE BOOK_T
   SET TITLE = #{title}
     , AUTHOR = #{author}
     , PRICE = #{price}
 WHERE BOOK_NO = #{bookNo}
