mapper.insert:
INSERT INTO MEMBER_T (
   MEMBER_NO
 , EMAIL
 , NAME
 , GENDER
 , ADDRESS
) VALUES (
    MEMBER_SEQ.NEXTVAL
  , #{email}
  , #{name}
  , #{gender}
  , #{address}
)

mapper.select:
SELECT MEMBER_NO, EMAIL, NAME, GENDER, ADDRESS
  FROM MEMBER_T
 WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER_T
   SET EMAIL = #{email}
     , NAME = #{name}
     , GENDER = #{gender}
     , ADDRESS = #{address}
 WHERE MEMBER_NO = #{memberNo}
